jQuery(document).ready(function(){applyProgresBar("body")});function applyProgresBar(context){jQuery(context).find(".progress").not(".ui-progressbar").each(function(){var progress=jQuery(this);var title=progress.text();var value=parseInt(title.replace("%",""));progress.text("");progress.progressbar({value:value});progress.find(".ui-progressbar-value").append("<div>"+title+"</div>")})}
